# unit testing
from app import decode_image_base64


# Unit tests for the decoding function
def test_decode_image_base64():
    # Test case 1: Valid base64 image data
    valid_image_data = "data:image/png;base64,"
    decoded_image = decode_image_base64(valid_image_data)
    assert decoded_image is not None

    # Test case 2: Invalid base64 image data
    invalid_image_data = "invalid_base64_data"
    decoded_image = decode_image_base64(invalid_image_data)
    assert decoded_image is None

    # Add more test cases as needed

# Run the unit tests if this script is executed directly
if __name__ == '__main__':
    test_decode_image_base64()
    print("Unit tests completed.")
